package com.mis.user.backstagemanagement.service.iml;import com.mis.user.commom.canstants.Canstants;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.mail.javamail.JavaMailSender;import org.springframework.mail.javamail.MimeMessageHelper;import org.springframework.stereotype.Service;import javax.mail.MessagingException;import javax.mail.internet.MimeMessage;@Servicepublic class UserAdviceMailServiceIml {    Logger logger = LoggerFactory.getLogger(this.getClass());    @Autowired    private JavaMailSender mailSender;    @Value("${spring.mail.username}")    private String mail;    /**     * @param subject     * @param content     *     * 客户意见反馈邮件发送     * */    public Object adviceMail(String subject, String content) {        MimeMessage message = null;        try {            message = mailSender.createMimeMessage();            MimeMessageHelper helper = new MimeMessageHelper(message, true);            helper.setFrom(mail);            helper.setTo(mail);            helper.setSubject(subject);            helper.setText(content, true);            mailSender.send(message);            return null;        } catch (MessagingException e) {            logger.error(e.getClass()+"{}",e);            return 0;        }    }}